mapper.select:
select
id, order_number, user_id, username, company_id, company_name, status, create_time, shipper_name,
shipper_phone_number, shipper_address, receiver_name, receiver_phone_number, receiver_address, remark
from logistics_order_management.transport_order


        and id = #{id}


        and order_number = #{orderNumber}


        and user_id = #{userId}


        and username = #{username}


        and company_id = #{companyId}


        and company_name = #{companyName}


        and status = #{status}


        and create_time = #{createTime}


        and shipper_name = #{shipperName}


        and shipper_phone_number = #{shipperPhoneNumber}


        and shipper_address = #{shipperAddress}


        and receiver_name = #{receiverName}


        and receiver_phone_number = #{receiverPhoneNumber}


        and receiver_address = #{receiverAddress}


        and remark = #{remark}
